import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './pages/Login';
import Registro from './pages/Registro';
import Reservar from './pages/Reservar';
import MisTurnos from './pages/MisTurnos';
import Admin from './pages/Admin';
import './styles/App.css';

function App() {
    const [user, setUser] = useState(null);

    useEffect(() => {
        const token = localStorage.getItem('token');
        const userData = localStorage.getItem('user');

        if (token && userData) {
            setUser(JSON.parse(userData));
        }
    }, []);

    const ProtectedRoute = ({ children }) => {
        return user ? children : <Navigate to="/login" />;
    };

    return (
        <Router future={{ v7_startTransition: true, v7_relativeSplatPath: true }}>
            <div className="App font-display bg-background-light dark:bg-background-dark text-text-light dark:text-text-dark">
                <Routes>
                    <Route 
                        path="/" 
                        element={
                            user ? <Navigate to="/reservar" /> : <Navigate to="/login" />
                        } 
                    />

                    <Route 
                        path="/login" 
                        element={
                            user ? <Navigate to="/reservar" /> : <Login setUser={setUser} />
                        } 
                    />

                    <Route 
                        path="/registro" 
                        element={
                            user ? <Navigate to="/reservar" /> : <Registro />
                        } 
                    />

                    <Route 
                        path="/reservar" 
                        element={
                            <ProtectedRoute>
                                <Reservar user={user} setUser={setUser} />
                            </ProtectedRoute>
                        } 
                    />

                    <Route 
                        path="/mis-turnos" 
                        element={
                            <ProtectedRoute>
                                <MisTurnos user={user} setUser={setUser} />
                            </ProtectedRoute>
                        } 
                    />

                    <Route 
                        path="/admin" 
                        element={
                            <ProtectedRoute>
                                <Admin user={user} setUser={setUser} />
                            </ProtectedRoute>
                        } 
                    />
                </Routes>
            </div>
        </Router>
    );
}

export default App;
