import React, { useState, useEffect } from 'react';
import api from '../services/api';

const MisTurnos = ({ user }) => {
    const [turnos, setTurnos] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        cargarMisTurnos();
    }, [user]);

    const cargarMisTurnos = async () => {
        try {
            const data = await api.misTurnos(user.id);
            setTurnos(data);
        } catch (error) {
            setError('Error al cargar turnos');
        } finally {
            setLoading(false);
        }
    };

    const cancelarTurno = async (id) => {
        if (window.confirm('¿Estás seguro de cancelar este turno?')) {
            try {
                await api.cancelarTurno(id);
                cargarMisTurnos();
            } catch (error) {
                setError('Error al cancelar turno');
            }
        }
    };

    const getEstadoBadge = (estado) => {
        const badges = {
            'reservado': 'bg-success',
            'cancelado': 'bg-danger',
            'completado': 'bg-primary'
        };
        return badges[estado] || 'bg-secondary';
    };

    if (loading) {
        return (
            <div className="container mt-4">
                <div className="text-center">
                    <div className="spinner-border" role="status">
                        <span className="visually-hidden">Cargando...</span>
                    </div>
                </div>
            </div>
        );
    }

    return (
        <div className="container mt-4">
            <h2>Mis Turnos</h2>

            {error && (
                <div className="alert alert-danger">{error}</div>
            )}

            {turnos.length === 0 ? (
                <div className="alert alert-info">
                    No tienes turnos reservados.
                </div>
            ) : (
                <div className="row">
                    {turnos.map(turno => (
                        <div key={turno.id} className="col-md-6 mb-3">
                            <div className="card">
                                <div className="card-header d-flex justify-content-between align-items-center">
                                    <h6 className="mb-0">{turno.servicio}</h6>
                                    <span className={`badge ${getEstadoBadge(turno.estado)}`}>
                                        {turno.estado.charAt(0).toUpperCase() + turno.estado.slice(1)}
                                    </span>
                                </div>
                                <div className="card-body">
                                    <p className="card-text">
                                        <strong>Fecha:</strong> {new Date(turno.fecha).toLocaleDateString('es-ES')}<br/>
                                        <strong>Hora:</strong> {turno.hora}<br/>
                                        {turno.observaciones && (
                                            <>
                                                <strong>Observaciones:</strong> {turno.observaciones}<br/>
                                            </>
                                        )}
                                        <small className="text-muted">
                                            Creado: {new Date(turno.created_at).toLocaleDateString('es-ES')}
                                        </small>
                                    </p>

                                    {turno.estado === 'reservado' && (
                                        <button 
                                            className="btn btn-danger btn-sm"
                                            onClick={() => cancelarTurno(turno.id)}
                                        >
                                            Cancelar Turno
                                        </button>
                                    )}
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default MisTurnos;
