import React, { useState, useEffect } from 'react';
import api from '../services/api';

const Admin = ({ user }) => {
    const [turnos, setTurnos] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        if (user?.rol === 'admin') {
            cargarTodosTurnos();
        }
    }, [user]);

    const cargarTodosTurnos = async () => {
        try {
            const data = await api.listarTurnos();
            setTurnos(data);
        } catch (error) {
            setError('Error al cargar turnos');
        } finally {
            setLoading(false);
        }
    };

    const cambiarEstadoTurno = async (id, nuevoEstado) => {
        try {
            await api.modificarTurno(id, { estado: nuevoEstado });
            cargarTodosTurnos();
        } catch (error) {
            setError('Error al modificar turno');
        }
    };

    if (user?.rol !== 'admin') {
        return (
            <div className="container mt-4">
                <div className="alert alert-warning">
                    No tienes permisos para acceder a esta página.
                </div>
            </div>
        );
    }

    if (loading) {
        return (
            <div className="container mt-4">
                <div className="text-center">
                    <div className="spinner-border" role="status">
                        <span className="visually-hidden">Cargando...</span>
                    </div>
                </div>
            </div>
        );
    }

    return (
        <div className="container mt-4">
            <h2>Panel de Administración</h2>

            {error && (
                <div className="alert alert-danger">{error}</div>
            )}

            <div className="card">
                <div className="card-header">
                    <h5>Gestión de Turnos</h5>
                </div>
                <div className="card-body">
                    <div className="table-responsive">
                        <table className="table">
                            <thead>
                                <tr>
                                    <th>ID</th>
                                    <th>Usuario</th>
                                    <th>Fecha</th>
                                    <th>Hora</th>
                                    <th>Servicio</th>
                                    <th>Estado</th>
                                    <th>Acciones</th>
                                </tr>
                            </thead>
                            <tbody>
                                {turnos.map(turno => (
                                    <tr key={turno.id}>
                                        <td>{turno.id}</td>
                                        <td>{turno.usuario_nombre || 'N/A'}</td>
                                        <td>{new Date(turno.fecha).toLocaleDateString('es-ES')}</td>
                                        <td>{turno.hora}</td>
                                        <td>{turno.servicio}</td>
                                        <td>
                                            <span className={`badge ${
                                                turno.estado === 'reservado' ? 'bg-success' :
                                                turno.estado === 'cancelado' ? 'bg-danger' : 'bg-primary'
                                            }`}>
                                                {turno.estado}
                                            </span>
                                        </td>
                                        <td>
                                            <div className="btn-group btn-group-sm">
                                                {turno.estado === 'reservado' && (
                                                    <>
                                                        <button 
                                                            className="btn btn-success"
                                                            onClick={() => cambiarEstadoTurno(turno.id, 'completado')}
                                                        >
                                                            Completar
                                                        </button>
                                                        <button 
                                                            className="btn btn-danger"
                                                            onClick={() => cambiarEstadoTurno(turno.id, 'cancelado')}
                                                        >
                                                            Cancelar
                                                        </button>
                                                    </>
                                                )}
                                                {turno.estado === 'cancelado' && (
                                                    <button 
                                                        className="btn btn-warning"
                                                        onClick={() => cambiarEstadoTurno(turno.id, 'reservado')}
                                                    >
                                                        Reactivar
                                                    </button>
                                                )}
                                            </div>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Admin;
