import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import api from '../services/api';

const Login = ({ setUser }) => {
    const [formData, setFormData] = useState({ email: '', password: '' });
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const [showPassword, setShowPassword] = useState(false);
    const [fieldErrors, setFieldErrors] = useState({});
    const navigate = useNavigate();

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });

        // Validación en tiempo real
        const errors = { ...fieldErrors };
        if (name === 'email') {
            if (!value) {
                errors.email = 'El email es obligatorio';
            } else if (!/\S+@\S+\.\S+/.test(value)) {
                errors.email = 'El email no es válido';
            } else {
                delete errors.email;
            }
        }
        if (name === 'password') {
            if (!value) {
                errors.password = 'La contraseña es obligatoria';
            } else if (value.length < 6) {
                errors.password = 'La contraseña debe tener al menos 6 caracteres';
            } else {
                delete errors.password;
            }
        }
        setFieldErrors(errors);
        if (error) setError('');
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');

        try {
            const response = await api.login(formData);

            if (response.token) {
                localStorage.setItem('token', response.token);
                localStorage.setItem('user', JSON.stringify(response.user));
                setUser(response.user);
                navigate('/reservar');
            } else {
                setError(response.error || 'Error al iniciar sesión');
            }
        } catch (error) {
            setError('Error de conexión');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="relative flex min-h-screen w-full flex-col group/design-root overflow-x-hidden">
            <div className="layout-container flex h-full grow flex-col">
                <main className="flex flex-1 items-center justify-center p-4 sm:p-6 md:p-8">
                    <div className="w-full max-w-5xl overflow-hidden rounded-xl bg-white dark:bg-gray-900/50 shadow-lg flex flex-col md:flex-row">
                        {/* Left Side - Form */}
                        <div className="w-full md:w-1/2 p-8 sm:p-12 flex flex-col justify-center">
                            <div className="max-w-md mx-auto w-full">
                                {/* Logo and Welcome Message */}
                                <div className="text-center md:text-left mb-10">
                                    <div className="flex items-center justify-center md:justify-start gap-2 mb-4">
                                        <span className="material-symbols-outlined text-primary text-4xl">
                                            clinical_notes
                                        </span>
                                        <h1 className="text-3xl font-bold text-gray-900 dark:text-white">Sonrisitapp</h1>
                                    </div>
                                    <h2 className="text-2xl font-bold tracking-tight text-gray-900 dark:text-white">Bienvenido de nuevo</h2>
                                    <p className="mt-2 text-sm text-gray-600 dark:text-gray-400">Gestionando sonrisas en tu comunidad.</p>
                                </div>

                                {/* Error Message */}
                                {error && (
                                    <div className="mb-6 p-4 rounded-lg bg-red-50 border border-red-200 text-red-700">
                                        {error}
                                    </div>
                                )}

                                {/* Form */}
                                <form className="space-y-6" onSubmit={handleSubmit}>
                                    {/* Email Field */}
                                    <div className="flex flex-col">
                                        <label className="text-sm font-medium leading-normal pb-2 text-gray-900 dark:text-gray-200" htmlFor="email">Correo Electrónico</label>
                                        <div className="flex w-full items-stretch rounded-lg">
                                            <input 
                                                className={`form-input flex w-full min-w-0 flex-1 resize-none overflow-hidden rounded-lg text-gray-900 dark:text-white focus:outline-0 focus:ring-2 focus:ring-primary/50 border ${fieldErrors.email ? 'border-red-300' : 'border-gray-300'} dark:border-gray-600 bg-background-light dark:bg-background-dark h-12 placeholder:text-gray-400 dark:placeholder:text-gray-500 p-3 rounded-r-none border-r-0 text-base font-normal leading-normal`}
                                                id="email" 
                                                name="email"
                                                placeholder="tu.correo@ejemplo.com" 
                                                type="email"
                                                value={formData.email}
                                                onChange={handleChange}
                                                required
                                            />
                                            <div className="text-gray-400 dark:text-gray-500 flex border border-gray-300 dark:border-gray-600 bg-background-light dark:bg-background-dark items-center justify-center pr-3 rounded-r-lg border-l-0">
                                                <span className="material-symbols-outlined">mail</span>
                                            </div>
                                        </div>
                                        {fieldErrors.email && (
                                            <p className="text-red-500 text-xs mt-1">{fieldErrors.email}</p>
                                        )}
                                    </div>

                                    {/* Password Field */}
                                    <div className="flex flex-col">
                                        <label className="text-sm font-medium leading-normal pb-2 text-gray-900 dark:text-gray-200" htmlFor="password">Contraseña</label>
                                        <div className="flex w-full items-stretch rounded-lg">
                                            <input 
                                                className={`form-input flex w-full min-w-0 flex-1 resize-none overflow-hidden rounded-lg text-gray-900 dark:text-white focus:outline-0 focus:ring-2 focus:ring-primary/50 border ${fieldErrors.password ? 'border-red-300' : 'border-gray-300'} dark:border-gray-600 bg-background-light dark:bg-background-dark h-12 placeholder:text-gray-400 dark:placeholder:text-gray-500 p-3 rounded-r-none border-r-0 text-base font-normal leading-normal`}
                                                id="password" 
                                                name="password"
                                                placeholder="Ingresa tu contraseña" 
                                                type={showPassword ? 'text' : 'password'}
                                                value={formData.password}
                                                onChange={handleChange}
                                                required
                                            />
                                            <button
                                                type="button"
                                                onClick={() => setShowPassword(!showPassword)}
                                                className="text-gray-400 dark:text-gray-500 flex border border-gray-300 dark:border-gray-600 bg-background-light dark:bg-background-dark items-center justify-center pr-3 rounded-r-lg border-l-0 hover:text-gray-600 dark:hover:text-gray-300 transition-colors"
                                            >
                                                <span className="material-symbols-outlined">
                                                    {showPassword ? 'visibility' : 'visibility_off'}
                                                </span>
                                            </button>
                                        </div>
                                        {fieldErrors.password && (
                                            <p className="text-red-500 text-xs mt-1">{fieldErrors.password}</p>
                                        )}
                                    </div>

                                    {/* Login Button */}
                                    <div>
                                        <button 
                                            type="submit"
                                            disabled={loading}
                                            className="flex w-full min-w-[84px] cursor-pointer items-center justify-center overflow-hidden rounded-lg h-12 px-5 bg-primary text-white text-base font-bold leading-normal tracking-[0.015em] hover:bg-primary/90 transition-colors duration-200 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary dark:focus:ring-offset-background-dark disabled:opacity-50"
                                        >
                                            <span className="truncate">
                                                {loading ? 'Iniciando...' : 'Iniciar Sesión'}
                                            </span>
                                        </button>
                                    </div>
                                </form>

                                {/* Links */}
                                <div className="mt-6 flex flex-col sm:flex-row items-center justify-between gap-3 text-center text-sm">
                                    <Link className="font-medium text-primary hover:text-primary/80" to="/recuperar-password">Olvidé mi contraseña</Link>
                                    <Link className="font-medium text-primary hover:text-primary/80" to="/registro">¿No tienes una cuenta? Regístrate</Link>
                                </div>
                            </div>
                        </div>

                        {/* Right Side - Image */}
                        <div className="hidden md:block md:w-1/2">
                            <div 
                                className="h-full w-full bg-cover bg-center" 
                                style={{
                                    backgroundImage: "url('https://lh3.googleusercontent.com/aida-public/AB6AXuAgqDe-6ZdtO6AaGHndMKlHlcSNp6qd0QVcA5GKM4PNyC2gvRRmNQX7NS74Co4G0HdUrOminXt7M-72J82YRRZT6ItvF1OrGBGZShr1GZZzY4-TPfo8n65e0POKlR8C39vvuIFPQFqErG5tiQzyCiQpIsJh9fMYKkyL2NxQhnhb44pg5uDEevRKV_e2EuC9zrSxxppweyY1IDP0ghXvvL7jZUBDYfujZoFfTsp782dUFHqMsvkVoG7O2APH2RWfIWrxDySx4amXMYc')"
                                }}
                            >
                            </div>
                        </div>
                    </div>
                </main>
            </div>
        </div>
    );
};

export default Login;
