import React, { useState, useEffect } from 'react';
import api from '../services/api';

const Reservar = ({ user }) => {
    const [turnos, setTurnos] = useState([]);
    const [formData, setFormData] = useState({
        fecha: '',
        hora: '',
        servicio: 'Limpieza dental',
        observaciones: ''
    });
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');
    const [loading, setLoading] = useState(false);

    const servicios = [
        'Limpieza dental',
        'Control general',
        'Ortodoncia',
        'Endodoncia',
        'Extracci贸n',
        'Blanqueamiento'
    ];

    const horarios = [
        '09:00', '10:00', '11:00', '14:00', '15:00', '16:00', '17:00'
    ];

    useEffect(() => {
        cargarTurnos();
    }, []);

    const cargarTurnos = async () => {
        try {
            const data = await api.listarTurnos();
            setTurnos(data);
        } catch (error) {
            setError('Error al cargar turnos');
        }
    };

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');
        setSuccess('');

        try {
            const turnoData = {
                ...formData,
                usuario_id: user.id
            };

            const response = await api.crearTurno(turnoData);

            if (response.id) {
                setSuccess('Turno reservado exitosamente');
                setFormData({
                    fecha: '',
                    hora: '',
                    servicio: 'Limpieza dental',
                    observaciones: ''
                });
                cargarTurnos();
            } else {
                setError(response.error || 'Error al reservar turno');
            }
        } catch (error) {
            setError('Error de conexi贸n');
        } finally {
            setLoading(false);
        }
    };

    const turnoOcupado = (fecha, hora) => {
        return turnos.some(turno => 
            turno.fecha === fecha && 
            turno.hora === hora && 
            turno.estado === 'reservado'
        );
    };

    return (
        <div className="container mt-4">
            <h2>Reservar Turno</h2>

            <div className="row">
                <div className="col-md-6">
                    <div className="card">
                        <div className="card-header">
                            <h5>Nuevo Turno</h5>
                        </div>
                        <div className="card-body">
                            {error && (
                                <div className="alert alert-danger">{error}</div>
                            )}
                            {success && (
                                <div className="alert alert-success">{success}</div>
                            )}

                            <form onSubmit={handleSubmit}>
                                <div className="mb-3">
                                    <label className="form-label">Fecha</label>
                                    <input
                                        type="date"
                                        className="form-control"
                                        name="fecha"
                                        value={formData.fecha}
                                        onChange={handleChange}
                                        min={new Date().toISOString().split('T')[0]}
                                        required
                                    />
                                </div>

                                <div className="mb-3">
                                    <label className="form-label">Hora</label>
                                    <select
                                        className="form-control"
                                        name="hora"
                                        value={formData.hora}
                                        onChange={handleChange}
                                        required
                                    >
                                        <option value="">Seleccionar hora</option>
                                        {horarios.map(hora => (
                                            <option 
                                                key={hora} 
                                                value={hora}
                                                disabled={turnoOcupado(formData.fecha, hora)}
                                            >
                                                {hora} {turnoOcupado(formData.fecha, hora) ? '(Ocupado)' : ''}
                                            </option>
                                        ))}
                                    </select>
                                </div>

                                <div className="mb-3">
                                    <label className="form-label">Servicio</label>
                                    <select
                                        className="form-control"
                                        name="servicio"
                                        value={formData.servicio}
                                        onChange={handleChange}
                                        required
                                    >
                                        {servicios.map(servicio => (
                                            <option key={servicio} value={servicio}>
                                                {servicio}
                                            </option>
                                        ))}
                                    </select>
                                </div>

                                <div className="mb-3">
                                    <label className="form-label">Observaciones</label>
                                    <textarea
                                        className="form-control"
                                        name="observaciones"
                                        value={formData.observaciones}
                                        onChange={handleChange}
                                        rows="3"
                                    />
                                </div>

                                <button 
                                    type="submit" 
                                    className="btn btn-primary"
                                    disabled={loading}
                                >
                                    {loading ? 'Reservando...' : 'Reservar Turno'}
                                </button>
                            </form>
                        </div>
                    </div>
                </div>

                <div className="col-md-6">
                    <div className="card">
                        <div className="card-header">
                            <h5>Turnos Disponibles</h5>
                        </div>
                        <div className="card-body">
                            <div className="table-responsive">
                                <table className="table table-sm">
                                    <thead>
                                        <tr>
                                            <th>Fecha</th>
                                            <th>Hora</th>
                                            <th>Estado</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {turnos.slice(0, 10).map(turno => (
                                            <tr key={turno.id}>
                                                <td>{turno.fecha}</td>
                                                <td>{turno.hora}</td>
                                                <td>
                                                    <span className={`badge ${
                                                        turno.estado === 'reservado' ? 'bg-danger' : 'bg-success'
                                                    }`}>
                                                        {turno.estado === 'reservado' ? 'Ocupado' : 'Disponible'}
                                                    </span>
                                                </td>
                                            </tr>
                                        ))}
                                    </tbody>
                                </table>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Reservar;
