import React, { useState } from 'react';
import Header from '../components/Header';
import Modal from '../components/Modal';
import api from '../services/api';

const Perfil = ({ user, setUser }) => {
    const [isEditing, setIsEditing] = useState(false);
    const [formData, setFormData] = useState({
        nombre: user?.nombre || '',
        email: user?.email || '',
        telefono: user?.telefono || '',
        currentPassword: '',
        newPassword: '',
        confirmPassword: ''
    });
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');
    const [loading, setLoading] = useState(false);

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
        if (error) setError('');
        if (success) setSuccess('');
    };

    const handleSave = async () => {
        setLoading(true);
        setError('');
        setSuccess('');

        try {
            // Validar contraseñas si se quiere cambiar
            if (formData.newPassword) {
                if (formData.newPassword !== formData.confirmPassword) {
                    setError('Las contraseñas nuevas no coinciden');
                    setLoading(false);
                    return;
                }
                if (formData.newPassword.length < 6) {
                    setError('La nueva contraseña debe tener al menos 6 caracteres');
                    setLoading(false);
                    return;
                }
            }

            // Simular actualización (necesitarías crear endpoint en backend)
            const updatedUser = {
                ...user,
                nombre: formData.nombre,
                email: formData.email,
                telefono: formData.telefono
            };

            // Actualizar localStorage y estado
            localStorage.setItem('user', JSON.stringify(updatedUser));
            setUser(updatedUser);

            setSuccess('Perfil actualizado correctamente');
            setIsEditing(false);

            // Limpiar campos de contraseña
            setFormData({
                ...formData,
                currentPassword: '',
                newPassword: '',
                confirmPassword: ''
            });
        } catch (error) {
            setError('Error al actualizar el perfil');
        } finally {
            setLoading(false);
        }
    };

    const handleCancel = () => {
        setFormData({
            nombre: user?.nombre || '',
            email: user?.email || '',
            telefono: user?.telefono || '',
            currentPassword: '',
            newPassword: '',
            confirmPassword: ''
        });
        setIsEditing(false);
        setError('');
        setSuccess('');
    };

    return (
        <div className="relative flex min-h-screen w-full flex-col group/design-root overflow-x-hidden">
            <Header user={user} setUser={setUser} />

            <main className="flex-grow container mx-auto px-4 sm:px-6 lg:px-8 py-10">
                <div className="max-w-2xl mx-auto">
                    {/* Header */}
                    <div className="flex items-center justify-between mb-8">
                        <div>
                            <h1 className="text-3xl font-bold text-gray-900 dark:text-white">Mi Perfil</h1>
                            <p className="text-gray-600 dark:text-gray-400 mt-1">
                                Gestiona tu información personal y configuración de cuenta
                            </p>
                        </div>
                        {!isEditing && (
                            <button
                                onClick={() => setIsEditing(true)}
                                className="flex items-center gap-2 px-4 py-2 bg-primary text-white rounded-lg hover:bg-primary/90 transition-colors"
                            >
                                <span className="material-symbols-outlined text-sm">edit</span>
                                Editar Perfil
                            </button>
                        )}
                    </div>

                    {/* Messages */}
                    {error && (
                        <div className="mb-6 p-4 rounded-lg bg-red-50 border border-red-200 text-red-700">
                            {error}
                        </div>
                    )}
                    {success && (
                        <div className="mb-6 p-4 rounded-lg bg-green-50 border border-green-200 text-green-700">
                            {success}
                        </div>
                    )}

                    {/* Profile Card */}
                    <div className="bg-white dark:bg-gray-900 rounded-xl shadow-sm border border-gray-200 dark:border-gray-700 overflow-hidden">
                        {/* Avatar Section */}
                        <div className="bg-gradient-to-r from-primary/10 to-primary/5 p-6 border-b border-gray-200 dark:border-gray-700">
                            <div className="flex items-center gap-4">
                                <div className="w-20 h-20 bg-primary/20 rounded-full flex items-center justify-center text-primary text-2xl font-bold">
                                    {user?.nombre?.charAt(0)?.toUpperCase()}
                                </div>
                                <div>
                                    <h2 className="text-xl font-semibold text-gray-900 dark:text-white">
                                        {user?.nombre}
                                    </h2>
                                    <p className="text-gray-600 dark:text-gray-400">{user?.email}</p>
                                    <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-green-100 text-green-800 dark:bg-green-900/50 dark:text-green-300 mt-1">
                                        {user?.rol === 'admin' ? 'Administrador' : 'Usuario'}
                                    </span>
                                </div>
                            </div>
                        </div>

                        {/* Form Section */}
                        <div className="p-6">
                            <div className="space-y-6">
                                {/* Información Personal */}
                                <div>
                                    <h3 className="text-lg font-medium text-gray-900 dark:text-white mb-4">
                                        Información Personal
                                    </h3>
                                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                                        <div>
                                            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                                                Nombre Completo
                                            </label>
                                            <input
                                                type="text"
                                                name="nombre"
                                                value={formData.nombre}
                                                onChange={handleChange}
                                                disabled={!isEditing}
                                                className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-primary focus:border-primary dark:bg-gray-800 dark:text-white disabled:bg-gray-50 dark:disabled:bg-gray-700 disabled:text-gray-500"
                                            />
                                        </div>

                                        <div>
                                            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                                                Teléfono
                                            </label>
                                            <input
                                                type="tel"
                                                name="telefono"
                                                value={formData.telefono}
                                                onChange={handleChange}
                                                disabled={!isEditing}
                                                className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-primary focus:border-primary dark:bg-gray-800 dark:text-white disabled:bg-gray-50 dark:disabled:bg-gray-700 disabled:text-gray-500"
                                                placeholder="Ej: +54 11 1234-5678"
                                            />
                                        </div>
                                    </div>
                                </div>

                                {/* Email */}
                                <div>
                                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                                        Correo Electrónico
                                    </label>
                                    <input
                                        type="email"
                                        name="email"
                                        value={formData.email}
                                        onChange={handleChange}
                                        disabled={!isEditing}
                                        className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-primary focus:border-primary dark:bg-gray-800 dark:text-white disabled:bg-gray-50 dark:disabled:bg-gray-700 disabled:text-gray-500"
                                    />
                                </div>

                                {/* Cambiar Contraseña */}
                                {isEditing && (
                                    <div>
                                        <h3 className="text-lg font-medium text-gray-900 dark:text-white mb-4">
                                            Cambiar Contraseña (Opcional)
                                        </h3>
                                        <div className="space-y-4">
                                            <div>
                                                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                                                    Contraseña Actual
                                                </label>
                                                <input
                                                    type="password"
                                                    name="currentPassword"
                                                    value={formData.currentPassword}
                                                    onChange={handleChange}
                                                    className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-primary focus:border-primary dark:bg-gray-800 dark:text-white"
                                                    placeholder="Ingresa tu contraseña actual"
                                                />
                                            </div>

                                            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                                                <div>
                                                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                                                        Nueva Contraseña
                                                    </label>
                                                    <input
                                                        type="password"
                                                        name="newPassword"
                                                        value={formData.newPassword}
                                                        onChange={handleChange}
                                                        className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-primary focus:border-primary dark:bg-gray-800 dark:text-white"
                                                        placeholder="Nueva contraseña"
                                                    />
                                                </div>

                                                <div>
                                                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                                                        Confirmar Contraseña
                                                    </label>
                                                    <input
                                                        type="password"
                                                        name="confirmPassword"
                                                        value={formData.confirmPassword}
                                                        onChange={handleChange}
                                                        className="w-full px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-primary focus:border-primary dark:bg-gray-800 dark:text-white"
                                                        placeholder="Confirmar contraseña"
                                                    />
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                )}

                                {/* Buttons */}
                                {isEditing && (
                                    <div className="flex gap-3 pt-6 border-t border-gray-200 dark:border-gray-700">
                                        <button
                                            onClick={handleCancel}
                                            className="px-4 py-2 text-sm font-medium text-gray-700 dark:text-gray-300 bg-gray-100 dark:bg-gray-800 rounded-lg hover:bg-gray-200 dark:hover:bg-gray-700 transition-colors"
                                        >
                                            Cancelar
                                        </button>
                                        <button
                                            onClick={handleSave}
                                            disabled={loading}
                                            className="px-4 py-2 text-sm font-medium text-white bg-primary rounded-lg hover:bg-primary/90 transition-colors disabled:opacity-50 flex items-center gap-2"
                                        >
                                            {loading && (
                                                <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin"></div>
                                            )}
                                            Guardar Cambios
                                        </button>
                                    </div>
                                )}
                            </div>
                        </div>
                    </div>

                    {/* Account Info */}
                    <div className="mt-6 bg-gray-50 dark:bg-gray-800/50 rounded-lg p-4">
                        <h4 className="font-medium text-gray-900 dark:text-white mb-2">Información de la Cuenta</h4>
                        <div className="text-sm text-gray-600 dark:text-gray-400 space-y-1">
                            <p>Cuenta creada: {new Date(user?.created_at || Date.now()).toLocaleDateString('es-ES')}</p>
                            <p>Último acceso: {new Date().toLocaleDateString('es-ES')}</p>
                        </div>
                    </div>
                </div>
            </main>
        </div>
    );
};

export default Perfil;
